let mostrarNotas=false;function loadCalificaciones(){fetch("ajax_calificaciones.php").then(e=>e.json()).then(e=>{const t=document.createElement("ul");t.className="list-group",e.forEach(e=>{const s=document.createElement("li");s.className="list-group-item",s.textContent=`${e.nombre_equipo} - Promedio: ${null!==e.promedio?e.promedio.toFixed(2):"N/A"}`,t.appendChild(s)}),document.getElementById("panel-calificaciones").appendChild(t)}).catch(e=>console.error("Error loading calificaciones:",e))}document.addEventListener("DOMContentLoaded",function(){loadCalificaciones(),document.getElementById("btn-toggle-notas").addEventListener("click",function(){const e=document.getElementById("panel-calificaciones");e.classList.toggle("blur-notas"),mostrarNotas=!mostrarNotas,this.textContent=mostrarNotas?"Ocultar notas":"Mostrar notas"})});
